<script>
document.addEventListener('DOMContentLoaded', function() {
    const form = document.getElementById('leadForm2');
    const submitBtn = document.getElementById('submitBtn2');
    const messageDiv = document.getElementById('formMessage2');
    const timeSpentField = form.querySelector('[name="timeSpentbeforeFormSubmit"]');
    const startTime = new Date().getTime();
    
    // Validate ALL required fields including Comments
    function validateForm() {
        let missingFields = [];
        
        // Title validation
        const title = form.querySelector('[name="Title"]').value;
        if (!title) {
            missingFields.push('Title');
        }
        
        // First Name validation - note the space in the name attribute
        const firstName = form.querySelector('[name="First Name"]').value.trim();
        if (!firstName) {
            missingFields.push('First Name');
        }
        
        // Last Name validation - note the space in the name attribute
        const lastName = form.querySelector('[name="Last Name"]').value.trim();
        if (!lastName) {
            missingFields.push('Last Name');
        }
        
        // Email validation
        const email = form.querySelector('[name="Email"]').value.trim();
        if (!email) {
            missingFields.push('Email');
        } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
            alert('Please enter a valid email address');
            return false;
        }
        
        // Phone validation
        const phone = form.querySelector('[name="Phone"]').value.trim();
        if (!phone) {
            missingFields.push('Phone');
        }
        
        // Comments validation - NOW REQUIRED
        const comments = form.querySelector('[name="Comments"]').value.trim();
        if (!comments) {
            missingFields.push('Comments');
        }
        
        // If any fields are missing, show alert with all missing fields
        if (missingFields.length > 0) {
            alert('Please fill in the following required fields:\n• ' + missingFields.join('\n• '));
            return false;
        }
        
        return true;
    }
    
    // Update time spent
    const timer = setInterval(function() {
        const currentTime = new Date().getTime();
        const timeSpent = Math.floor((currentTime - startTime) / 1000);
        if (timeSpentField) {
            timeSpentField.value = timeSpent;
        }
    }, 1000);
    
    // Comprehensive URL parameter mapping
    const urlParams = new URLSearchParams(window.location.search);
    const paramMap = {
        // UTM Parameters
        'utm_source': 'utmSource',
        'utm_medium': 'utmMedium',
        'utm_campaign': 'utmCampaign',
        'utm_content': 'utmContent',
        'utm_term': 'utmTerm',
        
        // Campaign Parameters
        'campaign_id': 'campaignId',
        'campaignid': 'campaignId',
        'campaign': 'campaignId',
        'websource': 'webSource',
        'web_source': 'webSource',
        'adgroup': 'adGroup',
        'ad_group': 'adGroup',
        'campaign_name': 'campaignName',
        'campaignname': 'campaignName',
        'goal': 'goal',
        'digital_source': 'digitalSource',
        'digitalsource': 'digitalSource',
        'channelcluster': 'channelCluster',
        'channel_cluster': 'channelCluster',
        'bannersize': 'bannerSize',
        'banner_size': 'bannerSize',
        'keyword': 'keyword',
        'placement': 'placement',
        'adposition': 'adPosition',
        'ad_position': 'adPosition',
        'matchtype': 'matchType',
        'match_type': 'matchType',
        'network': 'network',
        'bidtype': 'bidType',
        'bid_type': 'bidType',
        
        // Tracking Parameters
        'gclid': 'GCLID',
        'fbclid': 'fbclid',
        'refid': 'refid',
        'ref_id': 'refid',
        'ad_id': 'adid',
        'adid': 'adid',
        'ga_client_id': 'ga_client_id',
        'fbid': 'fbid',
        'fb_id': 'fbid',
        
        // Location Parameters
        'country': 'country',
        'city': 'city',
        'country_code': 'countryCode',
        'countrycode': 'countryCode',
        'country_name': 'countryNameSync',
        'countryname': 'countryNameSync',
        'city_sync': 'citySync',
        'citysync': 'citySync',
        'country_code_sync': 'countryCodeSync',
        'countrycodesync': 'countryCodeSync',
        
        // Project Parameters
        'project_name': 'projectName',
        'projectname': 'projectName',
        'project': 'projectName'
    };
    
    // Populate hidden fields from URL params
    for (const [param, field] of Object.entries(paramMap)) {
        if (urlParams.has(param)) {
            const input = form.querySelector(`[name="${field}"]`);
            if (input) {
                input.value = urlParams.get(param);
                console.log(`Set ${field} = ${urlParams.get(param)}`);
            }
        }
    }
    
    // Also check for any additional parameters not in the map
    for (const [key, value] of urlParams.entries()) {
        // Try to find a matching hidden field by name
        const input = form.querySelector(`[name="${key}"]`);
        if (input && !input.value) {
            input.value = value;
            console.log(`Set ${key} = ${value}`);
        }
    }
    
    // Set device and browser info
    const deviceInfo = {
        'device': /Mobile|iP(hone|od|ad)|Android|BlackBerry|IEMobile/.test(navigator.userAgent) ? 'Mobile' : 'Desktop',
        'os': navigator.platform,
        'resolution': window.screen.width + 'x' + window.screen.height,
        'browser': navigator.userAgent.split(' ').pop(),
        'user_agent': navigator.userAgent,
        'landingPageURL': window.location.href
    };
    
    for (const [field, value] of Object.entries(deviceInfo)) {
        const input = form.querySelector(`[name="${field}"]`);
        if (input) {
            input.value = value;
        }
    }
    
    // Set IP address field
    const ipField = form.querySelector('[name="ipAddress"]');
    if (ipField) {
        ipField.value = 'auto'; // Will be set by PHP
    }
    
    // Form submission
    form.addEventListener('submit', function(e) {
        e.preventDefault();
        
        // Validate ALL fields before submission
        if (!validateForm()) {
            return;
        }
        
        clearInterval(timer);
        
        submitBtn.disabled = true;
        submitBtn.textContent = 'SUBMITTING...';
        messageDiv.style.display = 'none';
        
        fetch(form.action, {
            method: 'POST',
            body: new FormData(form),
            headers: {
                'Accept': 'application/json'
            }
        })
        .then(response => response.json())
        .then(data => {
            if (data.success) {
                // Redirect immediately without showing message
                window.location.href = 'thank-you.html';
            } else {
                throw new Error(data.message || 'Submission failed');
            }
        })
        .catch(error => {
            alert('Error: ' + error.message);
        })
        .finally(() => {
            submitBtn.disabled = false;
            submitBtn.textContent = 'Get a call back!';
        });
    });
});
</script>
