<script>
document.addEventListener('DOMContentLoaded', function() {
    const form = document.getElementById('leadForm');
    const submitBtn = document.getElementById('submitBtn');
    const messageDiv = document.getElementById('formMessage');
    const timeSpentField = form.querySelector('[name="timeSpentbeforeFormSubmit"]');
    const startTime = new Date().getTime();
    
    // Validate form with alerts
    function validateForm() {
        // Title validation
        const title = form.querySelector('[name="Title"]').value;
        if (!title) {
            alert('Please select a title');
            return false;
        }
        
        // First Name validation
        const firstName = form.querySelector('[name="First_Name"]').value.trim();
        if (!firstName) {
            alert('Please fill in your First Name');
            return false;
        }
        
        // Last Name validation
        const lastName = form.querySelector('[name="Last_Name"]').value.trim();
        if (!lastName) {
            alert('Please fill in your Last Name');
            return false;
        }
        
        // Email validation
        const email = form.querySelector('[name="Email"]').value.trim();
        if (!email) {
            alert('Please fill in your Email');
            return false;
        } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
            alert('Please enter a valid email address');
            return false;
        }
        
        // Phone validation
        const phone = form.querySelector('[name="Phone"]').value.trim();
        if (!phone) {
            alert('Please fill in your Phone number');
            return false;
        }
        
        return true;
    }
    
    // Update time spent
    const timer = setInterval(function() {
        const currentTime = new Date().getTime();
        const timeSpent = Math.floor((currentTime - startTime) / 1000);
        if (timeSpentField) {
            timeSpentField.value = timeSpent;
        }
    }, 1000);
    
    // Comprehensive URL parameter mapping
    const urlParams = new URLSearchParams(window.location.search);
    const paramMap = {
        // UTM Parameters
        'utm_source': 'utmSource',
        'utm_medium': 'utmMedium',
        'utm_campaign': 'utmCampaign',
        'utm_content': 'utmContent',
        'utm_term': 'utmTerm',
        
        // Campaign Parameters
        'campaign_id': 'campaignId',
        'campaignid': 'campaignId',
        'campaign': 'campaignId',
        'websource': 'webSource',
        'web_source': 'webSource',
        'adgroup': 'adGroup',
        'ad_group': 'adGroup',
        'campaign_name': 'campaignName',
        'campaignname': 'campaignName',
        'goal': 'goal',
        'digital_source': 'digitalSource',
        'digitalsource': 'digitalSource',
        'channelcluster': 'channelCluster',
        'channel_cluster': 'channelCluster',
        'bannersize': 'bannerSize',
        'banner_size': 'bannerSize',
        'keyword': 'keyword',
        'placement': 'placement',
        'adposition': 'adPosition',
        'ad_position': 'adPosition',
        'matchtype': 'matchType',
        'match_type': 'matchType',
        'network': 'network',
        'bidtype': 'bidType',
        'bid_type': 'bidType',
        
        // Tracking Parameters
        'gclid': 'GCLID',
